package operators;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int a=9,b=5;
  System.out.println("and opr is:"+(a&b));
  System.out.println("or opr is:"+(a|b));
  System.out.println("xor opr is:"+(a^b));
  System.out.println("compliment opr is:"+(a-b));
 System.out.println("not opr is:"+~b);
 System.out.println(~4);
	}
	

}
