package operators;

public class LogicalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean a=true;
		boolean b=false;
		System.out.println("logical and oprtr is:"+(a&&b));
		System.out.println("logical or oprtr is:"+(a||b));
		System.out.println("logical not oprtr is:"+!a);
     
		
		System.out.println((5>3)&& (2>1));
	}

}
